.blog-con{
    width: 80%;
    margin: auto;
    display: flex;
    flex-direction: column;
    gap: 30px;
    align-items: start;
    margin-top: 250px;
    margin-bottom: 250px;
}

.blog-con img{
    width: 100%;
    border-radius: 15px;
}

/* Blog Content Specific Styles */
.blog-content {
    width: 100%;
    max-width: 800px;
    margin: 0 auto;
}

.blog-content h2 {
    font-size: 32px;
    font-weight: 600;
    color: white;
    margin-top: 40px;
    margin-bottom: 16px;
    line-height: 1.3;
}

.blog-content h3 {
    font-size: 24px;
    font-weight: 600;
    color: white;
    margin-top: 32px;
    margin-bottom: 12px;
    line-height: 1.4;
}

.blog-content h4 {
    font-size: 20px;
    font-weight: 500;
    color: rgba(255, 255, 255, 0.95);
    margin-top: 24px;
    margin-bottom: 10px;
    line-height: 1.5;
}

.blog-content p {
    font-size: 17px;
    font-weight: 400;
    color: rgba(255, 255, 255, 0.85);
    line-height: 1.7;
    margin-bottom: 20px;
}

.blog-content ul,
.blog-content ol {
    font-size: 17px;
    color: rgba(255, 255, 255, 0.85);
    line-height: 1.7;
    margin-bottom: 20px;
    padding-left: 30px;
}

.blog-content li {
    margin-bottom: 10px;
}

.blog-content a {
    color: rgb(100, 150, 255);
    text-decoration: underline;
    transition: color 0.3s ease;
}

.blog-content a:hover {
    color: rgb(150, 180, 255);
}

.blog-content blockquote {
    border-left: 4px solid rgba(255, 255, 255, 0.3);
    padding-left: 20px;
    margin: 24px 0;
    font-style: italic;
    color: rgba(255, 255, 255, 0.75);
}

.blog-content code {
    background-color: rgba(255, 255, 255, 0.1);
    padding: 2px 8px;
    border-radius: 4px;
    font-family: 'Courier New', monospace;
    font-size: 15px;
    color: rgba(255, 255, 255, 0.9);
}

.blog-content pre {
    background-color: rgba(255, 255, 255, 0.05);
    padding: 16px;
    border-radius: 8px;
    overflow-x: auto;
    margin-bottom: 20px;
}

.blog-content pre code {
    background-color: transparent;
    padding: 0;
}

.blog-content img {
    max-width: 100%;
    height: auto;
    border-radius: 10px;
    margin: 24px 0;
}

.blog-content strong {
    font-weight: 600;
    color: white;
}

.blog-content em {
    font-style: italic;
}

